import { Button } from './Button';
import { Link } from 'react-router-dom';
import './Navbar.css';
import Store from '../Redux/Store'
import {ACTION_USER_LOGOUT} from '../Redux/Action/UserAction'
import {connect} from 'react-redux'

var mapStateToProps = state => {
  return {
     user: state.user,
