import React, { Component } from "react";
import { Link, Redirect, NavLink  } from "react-router-dom";
import "./App.css";

import {
  GoogleLoginButton,
  FacebookLoginButton
} from "react-social-login-buttons";

class SignInForm extends Component {
  constructor() {
    super();

    this.state = {
      email: "",
      password: "",
      loginStatus:false
    }

  }

    login = (event)=>{
      var ob = {
       
          email:this.email.value,
          password: this.password.value,
         
      }
  
     /*  function login(){

        let history = useHistory();
     
      } */

      fetch(`http://localhost:9090/register/userLogin`,{
        method : 'POST',
        headers:{
            "Content-Type" : "application/json"
        },
        body : JSON.stringify(ob)
    }).then(response=>response.json()).then(data=>{
        console.log(data.code,data.data)
        if (data.code==200){
          console.log("success")
          this.setState({loginStatus:true})
        }
        
        this.setState({regmsg:data.data})
    });;
  
      console.log(ob)
      event.preventDefault()
  } 

  
  

  render() {
    if(this.state.loginStatus){
      return <Redirect to="/home"></Redirect>
    }
    return (



      <div className="App" >
      <div className="appAside" />
      <div className="appForm" >

        <div className="pageSwitcher">
          <NavLink
            to="/sign-in"
            activeClassName="pageSwitcherItem-active"
            className="pageSwitcherItem"
          >
            Sign In
          </NavLink>
          <NavLink
            exact
            to="/"
            activeClassName="pageSwitcherItem-active"
            className="pageSwitcherItem"
          >
            Sign Up
          </NavLink>
        </div>
 


      <div className="formCenter">
        <form className="formFields" onSubmit={this.login}>
          <div className="formField">
            <label className="formFieldLabel" htmlFor="email">
              E-Mail Address
            </label>
            <input
              type="email"
              ref={c=>this.email=c}
              id="email"
              className="formFieldInput"
              placeholder="Enter your email"
              name="email"
             
            />
          </div>

          <div className="formField">
            <label className="formFieldLabel" htmlFor="password">
              Password
            </label>
            <input
              type="password"
              ref={c=>this.password=c}
              id="password"
              className="formFieldInput"
              placeholder="Enter your password"
              name="password"
             
            />
          </div>

          <div className="formField">
            <button type="submit" name="login" class="login" value="login"className="formFieldButton">Sign In</button>{" "}
            <Link to="/" className="formFieldLink">
              Create an account
            </Link>
          </div>

          <div className="socialMediaButtons">
            <div className="googleButton">
              <GoogleLoginButton onClick={() => alert()} />
            </div>

            <div className="instagramButton">
              <FacebookLoginButton onClick={"https://www.facebook.com/"} />
             
            </div>
          </div>
        </form>
        </div>
      </div>
      </div>
    );
    }
}

export default SignInForm;
